import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import CardContent from '@material-ui/core/CardContent';
import Card from '@material-ui/core/Card';
import { Link } from 'react-router-dom';

class Recruiter extends React.Component {
  render() {
    const { classes,recruiter  } = this.props;
    const { companyName, description, lookingFor, id } = recruiter
    // console.log(this.props.recruiters[0].companyName)




    return (
      <section className={classes.root}>
      <Link className={classes.link} to={`/recruiters/${this.props.id}`}>
        <Grid item xs={12}>
          <Grid container direction="row" justify="space-around" alignItems="center">
            <Card>
              <CardContent boxShadow={3} className={classes.cardWidth}>
                <Typography variant="h5">{companyName}</Typography>
                <div>
                  <Typography className={classes.searchingFor}>
                    Searching for: {lookingFor}
                  </Typography>
                  <p>{type}</p>
                  <Button
                    onClick={(recruiter) => {
                      this.props.handleOpen(recruiter); //pass props here
                    }}
                    className={classes.button}
                    >
                    open profile
                  </Button>
                </div>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
        </Link>
      </section>
    );
  }
}

const styles = themes => ({
  root: {
    flexGrow: 1,
    width: 300,
    margin: '10px 0'
  },

  link: {
    textDecoration: 'none',
    color: themes.palette.primary.dark
  },

  cardWidth: {
    paddingTop: '25%',
    height: 200
  },

  searchingFor: {
    color: themes.palette.primary.light,
    width: 200
  },

  button: {
    backgroundColor: themes.palette.secondary.light,
    color: 'white',
    marginTop: 50,
  }
});

export default withStyles(styles)(Recruiter);
