import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import Button from '@material-ui/core/Button';
import Avatar from '@material-ui/core/Avatar';
import avatarImage from './grey-image.jpg'

class ProfileModal extends React.Component {
  render() {
    const { classes, selectedRecruiter: recruiter, handleClose, open } = this.props;

    return (
      <>
        {recruiter &&

          <section className={classes.root} >
            <Modal
              onClose={handleClose}
              open={open}
              className={classes.modal}>
              <Card className={classes.paper}>
                <Typography className={classes.name} variant="h3" id="modal-title">
                  {recruiter.companyName}
                </Typography>
                <Avatar 
                alt="Remy Sharp" 
                src="/static/images/avatar/1.jpg" 
                className={classes.avatar} />

                <div className={classes.textWrapper} >
                  <Typography variant="h4" id="simple-modal-description">

                  {recruiter.lookingFor}
                  </Typography>
                  <p>{recruiter.description}</p>
                  <Button className={classes.button}>
                  {recruiter.email}
                  </Button>
                </div>

              </Card>
            </Modal>

          </section>

        }
      </>
    );
  }
}

const styles = themes => ({
  root: {
    flexGrow: 1,
    width: 300,
    margin: '10px 0'
  },

  name: {
    color: themes.palette.primary.dark,
    paddingBottom: 30,
  },


  modal: {
    position: 'absolute',
    width: 450,
    textAlign: 'center',
    margin: '0 auto',
    top: '30%',
    [themes.breakpoints.down('sm')]: {
      width: '75%'
    }
  },

  textWrapper: {
    textAlign: 'left',
  },

  paper: {
    height: 500,
    padding: 40,
  },

  button: {
    backgroundColor: themes.palette.primary.main,
    color: themes.palette.secondary.contrastText,
  }
});

export default withStyles(styles)(ProfileModal);
